
var sql = require('fusion/sql');

exports.begin = function(gift) {
  var res = sql.query('INSERT INTO contributions(ItemID,`From`,Email,Message,Amount) VALUES (:)',[gift.itemID,gift.from,gift.email,gift.message,gift.amount]);
  return res.insertId;
}

exports.finalize = function(contribID) {
  sql.query('UPDATE contributions SET Status=1 WHERE ContribID=?',arguments);
}

