
var sql = require('fusion/sql');

exports.getData = function() {
  return sql.data('SELECT ItemID,Category,Title,Contributed,Price,img FROM items');
}

exports.update = function(itemID,contributed) {
  sql.query('UPDATE items SET Contributed=Contributed+? WHERE ItemID=?',[contributed,itemID]);
}

exports.addImageURL = function(itemID,url) {
  sql.query('UPDATE items SET URL=? WHERE ItemID=?',[url,itemID]);
}

